def environment(world, x, y)
  sum = 0
  area = [world[y-1][x-1], world[y-1][x], world[y-1][x+1], world[y][x-1], world[y][x+1], world[y+1][x-1], world[y+1][x], world[y+1][x+1]]

  area.each do |cell|
    if cell == '█'
      sum += 1
    end
  end

  return sum

end

 10000.times do
  y = 1
  while y < 49

    x = 1
    while x < 119

      if world[y][x] == ' ' && environment(world, x, y) == 3
        world[y][x] = '█'
      end

      if world[y][x] == '█' && 1 < environment(world, x, y) && environment(world, x, y) < 4
        world[y][x] = '█'
      else
        world[y][x] = ' '
      end

      x += 1
    end

    y += 1
  end


  world.each do |cell|
    puts cell.join('')
  end

sleep 0.2
system "clear"

end
